CREATE TABLE IF NOT EXISTS mailing_list (
    `Channel_Name` VARCHAR(42) CHARACTER SET utf8,
    `category` VARCHAR(20) CHARACTER SET utf8,
    `country` VARCHAR(6) CHARACTER SET utf8,
    `Email` VARCHAR(35) CHARACTER SET utf8,
    `Average_Views` VARCHAR(6) CHARACTER SET utf8,
    `Link` VARCHAR(67) CHARACTER SET utf8
);
